INSERT INTO `modx_site_htmlsnippets` (`id`, `source`, `property_preprocess`, `name`, `description`, `editor_type`, `category`, `cache_type`, `snippet`, `locked`, `properties`, `static`, `static_file`) VALUES
(1, 1, 0, 'tpl.Tickets.form.create', '', 0, 8, 0, '<form class="well create" method="post" action="" id="ticketForm">\n	<div id="ticket-preview-placeholder"></div>\n\n	<input type="hidden" name="tid" value="0" />\n\n	<div class="form-group">\n		<label for="ticket-sections">[[%tickets_section]]</label>\n		<select name="parent" class="form-control" id="ticket-sections">[[+sections]]</select>\n		<span class="error"></span>\n	</div>\n\n	<div class="form-group">\n		<label for="ticket-pagetitle">[[%ticket_pagetitle]]</label>\n		<input type="text" class="form-control" placeholder="[[%ticket_pagetitle]]" name="pagetitle" value="" maxlength="50" id="ticket-pagetitle"/>\n		<span class="error"></span>\n	</div>\n\n	<div class="form-group">\n		<textarea class="form-control" placeholder="[[%ticket_content]]" name="content" id="ticket-editor" rows="10"></textarea>\n		<span class="error"></span>\n	</div>\n\n	<div class="ticket-form-files">\n		[[+files]]\n	</div>\n\n	<div class="form-actions row">\n		<div class="col-md-6">\n			<input type="button" class="btn btn-default preview" value="[[%ticket_preview]]" title="Ctrl + Enter" />\n		</div>\n		<div class="col-md-6 move-right">\n			<input type="button" class="btn btn-primary publish" name="publish" value="[[%ticket_publish]]" title="" />\n			<input type="submit" class="btn btn-danger draft" name="draft" value="[[%ticket_draft]]" title="Ctrl + Shift + Enter" />\n		</div>\n	</div>\n</form>', 0, NULL, 0, 'core/components/tickets/elements/chunks/chunk.form_create.tpl'),
(2, 1, 0, 'tpl.Tickets.form.update', '', 0, 8, 0, '<form class="well update" method="post" action="" id="ticketForm">\n	<div id="ticket-preview-placeholder"></div>\n\n	<input type="hidden" name="tid" value="[[+id]]" />\n\n	<div class="form-group">\n		<label for="ticket-sections">[[%tickets_section]]</label>\n		<select name="parent" class="form-control" id="ticket-sections">[[+sections]]</select>\n		<span class="error"></span>\n	</div>\n\n	<div class="form-group">\n		<label for="ticket-pagetitle">[[%ticket_pagetitle]]</label>\n		<input type="text" class="form-control" placeholder="[[%ticket_pagetitle]]" name="pagetitle" value="[[+pagetitle]]" maxlength="50" id="ticket-pagetitle"/>\n		<span class="error"></span>\n	</div>\n\n	<div class="form-group">\n		<textarea class="form-control" placeholder="[[%ticket_content]]" name="content" id="ticket-editor" rows="10">[[+content]]</textarea>\n		<span class="error"></span>\n	</div>\n\n	<div class="ticket-form-files">\n		[[+files]]\n	</div>\n\n	<div class="form-actions row">\n		<div class="col-md-6">\n			<input type="button" class="btn btn-default preview" value="[[%ticket_preview]]" title="Ctrl + Enter" />\n		</div>\n		<div class="col-md-6 move-right">\n			[[!+published:is=`1`:then=`\n			<a href="[[~[[+id]]?scheme=`full`]]" class="btn btn-default btn-xs" target="_blank">[[%ticket_open]]</a>\n			<input type="button" class="btn btn-danger draft" name="draft" value="[[%ticket_draft]]" title="" />\n			`:else=`\n			<input type="button" class="btn btn-primary publish" name="publish" value="[[%ticket_publish]]" title="" />\n			`]]\n			<input type="submit" class="btn btn-default save" name="save" value="[[%ticket_save]]" title="Ctrl + Shift + Enter" />\n		</div>\n	</div>\n</form>', 0, NULL, 0, 'core/components/tickets/elements/chunks/chunk.form_update.tpl'),
(3, 1, 0, 'tpl.Tickets.form.preview', '', 0, 8, 0, '<h3 class="title">[[+pagetitle]]</h3>\n<div class="content">[[+content]]</div>\n<h5 class="author">[[+modx.user.id:userinfo=`username`]]</h5>\n', 0, NULL, 0, 'core/components/tickets/elements/chunks/chunk.form_preview.tpl'),
(4, 1, 0, 'tpl.Tickets.form.files', '', 0, 8, 0, '<div id="ticket-files-list">\n	[[+files]]\n	<div class="clearfix"></div>\n</div>\n\n<div id="ticket-files-container" data-action="ticket/file/upload">\n	<a id="ticket-files-select" href="javascript:;">[[%ticket_file_select]]</a>\n	<div id="ticket-files-progress">\n		<span id="ticket-files-progress-count">0/0</span>\n		<span id="ticket-files-progress-percent">0%</span>\n		<div id="ticket-files-progress-bar"></div>\n	</div>\n</div>', 0, NULL, 0, 'core/components/tickets/elements/chunks/chunk.form_files.tpl'),
(5, 1, 0, 'tpl.Tickets.form.file', '', 0, 8, 0, '<div class="ticket-file[[+deleted]][[+new]]" data-id="[[+id]]">\n	<a href="[[+url]]" class="ticket-file-link" title="[[+file]]" target="_blank">\n		<div class="ticket-file-image-wrapper">\n			[[+file]]\n		</div>\n	</a>\n	<div class="ticket-file-meta">\n		<a href="#" class="ticket-file-delete">[[%ticket_file_delete]]</a>\n		<a href="#" class="ticket-file-restore">[[%ticket_file_restore]]</a>\n		<br/>\n		<a href="#" class="ticket-file-insert">[[%ticket_file_insert]]</a>\n		<br/>\n		<span class="ticket-file-size">[[+size]] Kb</span>\n	</div>\n	<div class="ticket-file-template">\n		<a href="[[+url]]">\n			[[+name]]\n		</a>\n	</div>\n</div>\n<!--tickets_thumb <img src="[[+thumb]]" class="ticket-file-image" />-->\n<!--tickets_deleted  deleted-->\n<!--tickets_new  new-->', 0, NULL, 0, 'core/components/tickets/elements/chunks/chunk.form_file.tpl'),
(6, 1, 0, 'tpl.Tickets.form.image', '', 0, 8, 0, '<div class="ticket-file[[+deleted]][[+new]]" data-id="[[+id]]">\n	<a href="[[+url]]" class="ticket-file-link" title="[[+file]]" target="_blank">\n		<div class="ticket-file-image-wrapper">\n			<img src="[[+thumb]]" class="ticket-file-image" />\n		</div>\n	</a>\n	<div class="ticket-file-meta">\n		<a href="#" class="ticket-file-delete">[[%ticket_file_delete]]</a>\n		<a href="#" class="ticket-file-restore">[[%ticket_file_restore]]</a>\n		<br/>\n		<a href="#" class="ticket-file-insert">[[%ticket_file_insert]]</a>\n		<br/>\n		<span class="ticket-file-size">[[+size]] Kb</span>\n	</div>\n	<div class="ticket-file-template">\n		<a href="[[+url]]" title="[[+name]]">\n			<img src="[[+thumb]]" />\n		</a>\n	</div>\n</div>\n<!--tickets_deleted  deleted-->\n<!--tickets_new  new-->', 0, NULL, 0, 'core/components/tickets/elements/chunks/chunk.form_image.tpl'),
(7, 1, 0, 'tpl.Tickets.ticket.latest', '', 0, 8, 0, '<div class="tickets-latest-row">\n	<span class="user"><i class="glyphicon glyphicon-user"></i> [[+fullname]]</span> <span class="date">[[+date_ago]]</span>\n	<br/>\n	<span class="section">\n		<i class="glyphicon glyphicon-folder-open"></i> <a href="[[~[[+section.id]]]]">[[+section.pagetitle]]</a> <span class="arrow">&rarr;</span>\n	</span>\n	<span class="ticket">\n		<a href="[[~[[+id]]]]">[[+pagetitle]]</a>\n	</span>\n	<nobr><i class="glyphicon glyphicon-comment"></i> <span class="comments">[[+comments]]</span></nobr>\n</div>', 0, NULL, 0, 'core/components/tickets/elements/chunks/chunk.ticket_latest.tpl'),
(8, 1, 0, 'tpl.Tickets.ticket.email.bcc', '', 0, 8, 0, '[[%ticket_email_bcc_intro?\n    &fullname=`[[+user.fullname]]`\n    &email=`[[+user.email]]`\n    &id=`[[+id]]`\n    &pagetitle=`[[+pagetitle]]`\n]]\n\n<pre style="background-color:#efefef;">[[+introtext]]</pre>\n<br/><br/>\n\n<a href="[[~[[+id]]?scheme=`full`]]">[[%ticket_email_view]]</a>', 0, NULL, 0, 'core/components/tickets/elements/chunks/chunk.ticket_email_bcc.tpl'),
(9, 1, 0, 'tpl.Tickets.ticket.email.subscription', '', 0, 8, 0, '[[%ticket_email_subscribed_intro?\n    &id=`[[+id]]`\n    &fullname=`[[+user.fullname]]`\n    &section=`[[+section.id]]`\n    &section_title=`[[+section.pagetitle]]`\n    &pagetitle=`[[+pagetitle]]`\n]]\n\n<pre style="background-color:#efefef;">[[+introtext]]</pre>\n<br/><br/>\n\n<a href="[[~[[+id]]?scheme=`full`]]">[[%ticket_email_view]]</a>', 0, NULL, 0, 'core/components/tickets/elements/chunks/chunk.ticket_email_subscription.tpl'),
(10, 1, 0, 'tpl.Tickets.comment.form', '', 0, 8, 0, '<h4 id="comment-new-link">\n	<a href="#" class="btn btn-default">[[%ticket_comment_create]]</a>\n</h4>\n\n<div id="comment-form-placeholder">\n	<form id="comment-form" action="" method="post" class="well">\n		<div id="comment-preview-placeholder"></div>\n		<input type="hidden" name="thread" value="[[+thread]]" />\n		<input type="hidden" name="parent" value="0" />\n		<input type="hidden" name="id" value="0" />\n\n		<div class="form-group">\n			<label for="comment-editor"></label>\n			<textarea name="text" id="comment-editor" cols="30" rows="10" class="form-control"></textarea>\n		</div>\n\n		<div class="form-actions">\n			<input type="button" class="btn btn-default preview" value="[[%ticket_comment_preview]]" title="Ctrl + Enter" />\n			<input type="submit" class="btn btn-primary submit" value="[[%ticket_comment_save]]" title="Ctrl + Shift + Enter" />\n			<span class="time"></span>\n		</div>\n	</form>\n</div>', 0, NULL, 0, 'core/components/tickets/elements/chunks/chunk.comment_form.tpl'),
(11, 1, 0, 'tpl.Tickets.comment.form.guest', '', 0, 8, 0, '<h4 id="comment-new-link">\n	<a href="#" class="btn btn-default">[[%ticket_comment_create]]</a>\n</h4>\n\n<div id="comment-form-placeholder">\n	<form id="comment-form" action="" method="post" class="well">\n		<div id="comment-preview-placeholder"></div>\n		<input type="hidden" name="thread" value="[[+thread]]" />\n		<input type="hidden" name="parent" value="0" />\n		<input type="hidden" name="id" value="0" />\n\n		<div class="form-group">\n			<label for="comment-name">[[%ticket_comment_name]]</label>\n			<input type="text" name="name" value="[[+name]]" id="comment-name" class="form-control" />\n			<span class="error"></span>\n		</div>\n\n		<div class="form-group">\n			<label for="comment-email">[[%ticket_comment_email]]</label>\n			<input type="text" name="email" value="[[+email]]" id="comment-email" class="form-control" />\n			<span class="error"></span>\n		</div>\n\n		<div class="form-group">\n			<label for="comment-editor"></label>\n			<textarea name="text" id="comment-editor" cols="30" rows="10" class="form-control"></textarea>\n		</div>\n\n		[[+captcha]]\n\n		<div class="form-actions">\n			<input type="button" class="btn btn-default preview" value="[[%ticket_comment_preview]]" title="Ctrl + Enter" />\n			<input type="submit" class="btn btn-primary submit" value="[[%ticket_comment_save]]" title="Ctrl + Shift + Enter" />\n			<span class="time"></span>\n		</div>\n	</form>\n</div>\n\n<!--tickets_captcha\n<div class="form-group">\n	<label for="comment-captcha" id="comment-captcha">[[+captcha]]</label>\n	<input type="text" name="captcha" value="" id="comment-captcha" class="form-control" />\n	<span class="error"></span>\n</div>\n-->', 0, NULL, 0, 'core/components/tickets/elements/chunks/chunk.comment_form_guest.tpl'),
(12, 1, 0, 'tpl.Tickets.comment.one.auth', '', 0, 8, 0, '<li class="ticket-comment[[+comment_new]]" id="comment-[[+id]]" data-parent="[[+parent]]" data-newparent="[[+new_parent]]" data-id="[[+id]]">\n	<div class="ticket-comment-body[[+guest]][[+bad]]">\n		<div class="ticket-comment-header">\n			<div class="ticket-comment-dot-wrapper"><div class="ticket-comment-dot"></div></div>\n			<img src="[[+avatar]]" class="ticket-avatar" alt="" />\n			<span class="ticket-comment-author">[[+fullname]]</span>\n			<span class="ticket-comment-createdon">[[+date_ago]]</span>[[+comment_was_edited]]\n			<span class="ticket-comment-link"><a href="[[+url]]#comment-[[+id]]">#</a></span>\n			<span class="ticket-comment-star[[+can_star]]">[[+stared]][[+unstared]]</span>\n			[[+has_parent]]\n			<span class="ticket-comment-down"><a href="#" data-child="">&darr;</a></span>\n			<span class="ticket-comment-rating[[+can_vote]][[+cant_vote]]">\n				<span class="rating[[+rating_positive]][[+rating_negative]]" title="[[%ticket_rating_total]] [[+rating_total]]: ↑[[+rating_plus]] [[%ticket_rating_and]] ↓[[+rating_minus]]">[[+rating]]</span>\n				<span class="vote plus[[+voted_plus]]" title="[[%ticket_like]]"><i class="glyphicon glyphicon-arrow-up"></i></span>\n				<span class="vote minus[[+voted_minus]]" title="[[%ticket_dislike]]"><i class="glyphicon glyphicon-arrow-down"></i></span>\n			</span>\n		</div>\n		<div class="ticket-comment-text">\n			[[+text]]\n		</div>\n	</div>\n	<div class="comment-reply">\n		<a href="#" class="reply">[[%ticket_comment_reply]]</a>\n		[[+comment_edit_link]]\n	</div>\n	<ol class="comments-list">[[+children]]</ol>\n</li>\n<!--tickets_comment_edit_link <a href="#" class="edit">[[%ticket_comment_edit]]</a>-->\n<!--tickets_comment_was_edited <span class="ticket-comment-edited">([[%ticket_comment_was_edited]])</span>-->\n<!--tickets_comment_new  ticket-comment-new-->\n<!--tickets_can_vote  active-->\n<!--tickets_cant_vote  inactive-->\n<!--tickets_rating_positive  positive-->\n<!--tickets_rating_negative  negative-->\n<!--tickets_voted_plus  voted-->\n<!--tickets_voted_minus  voted-->\n<!--tickets_guest  ticket-comment-guest-->\n<!--tickets_has_parent <span class="ticket-comment-up"><a href="[[+url]]#comment-[[+parent]]" data-id="[[+id]]" data-parent="[[+parent]]">&uarr;</a></span>-->\n<!--tickets_can_star  active-->\n<!--tickets_stared <i class="glyphicon glyphicon-star stared star"></i>-->\n<!--tickets_unstared <i class="glyphicon glyphicon-star unstared star"></i>-->\n', 0, NULL, 0, 'core/components/tickets/elements/chunks/chunk.comment_one_auth.tpl'),
(13, 1, 0, 'tpl.Tickets.comment.one.guest', '', 0, 8, 0, '<li class="ticket-comment" id="comment-[[+id]]">\n	<div class="ticket-comment-body[[+bad]]">\n		<div class="ticket-comment-header">\n			<div class="ticket-comment-dot-wrapper"><div class="ticket-comment-dot"></div></div>\n			<img src="[[+avatar]]" class="ticket-avatar" alt="" />\n			<span class="ticket-comment-author">[[+fullname]]</span>\n			<span class="ticket-comment-createdon">[[+date_ago]]</span>\n			<span class="ticket-comment-link"><a href="[[+url]]#comment-[[+id]]">#</a></span>\n\n			[[+has_parent]]\n			<span class="ticket-comment-down"><a href="#" data-child="">&darr;</a></span>\n\n			<span class="ticket-comment-rating inactive">\n				<span class="rating[[+rating_positive]][[+rating_negative]]">\n					[[+rating]]\n				</span>\n				<span class="plus" title="[[%ticket_like]]">\n					<i class="glyphicon glyphicon-arrow-up"></i>\n				</span>\n				<span class="minus" title="[[%ticket_dislike]]">\n					<i class="glyphicon glyphicon-arrow-down"></i>\n				</span>\n			</span>\n		</div>\n		<div class="ticket-comment-text">\n			[[+text]]\n		</div>\n	</div>\n	<ol class="comments-list">[[+children]]</ol>\n</li>\n<!--tickets_rating_positive  positive-->\n<!--tickets_rating_negative  negative-->\n<!--tickets_has_parent <span class="ticket-comment-up"><a href="[[+url]]#comment-[[+parent]]" data-id="[[+id]]" data-parent="[[+parent]]">&uarr;</a></span>-->', 0, NULL, 0, 'core/components/tickets/elements/chunks/chunk.comment_one_guest.tpl'),
(14, 1, 0, 'tpl.Tickets.comment.one.deleted', '', 0, 8, 0, '<li class="ticket-comment" id="comment-[[+id]]">\n	<div class="ticket-comment-body alert alert-warning">\n		[[%ticket_comment_deleted_text]]\n	</div>\n	<ol class="comments-list">[[+children]]</ol>\n</li>\n', 0, NULL, 0, 'core/components/tickets/elements/chunks/chunk.comment_one_deleted.tpl'),
(15, 1, 0, 'tpl.Tickets.comment.wrapper', '', 0, 8, 0, '<div class="comments">\n	[[+modx.user.id:isloggedin:is=`1`:then=`\n	<span class="comments-subscribe pull-right">\n		<label for="comments-subscribe" class="checkbox">\n			<input type="checkbox" name="" id="comments-subscribe" value="1" [[+subscribed]] /> [[%ticket_comment_notify]]\n		</label>\n	</span>\n	`:else=``]]\n\n	<h3 class="title">[[%comments]] (<span id="comment-total">[[+total]]</span>)</h3>\n\n	<div id="comments-wrapper">\n		<ol class="comment-list" id="comments">[[+comments]]</ol>\n	</div>\n\n	<div id="comments-tpanel">\n		<div id="tpanel-refresh"></div>\n		<div id="tpanel-new"></div>\n	</div>\n</div>\n\n<!--tickets_subscribed checked-->\n', 0, NULL, 0, 'core/components/tickets/elements/chunks/chunk.comment_wrapper.tpl'),
(16, 1, 0, 'tpl.Tickets.comment.login', '', 0, 8, 0, '<div class="ticket-comments alert alert-warning">\n    <p>[[%ticket_comment_err_no_auth]]</p>\n</div>', 0, NULL, 0, 'core/components/tickets/elements/chunks/chunk.comment_login.tpl'),
(17, 1, 0, 'tpl.Tickets.comment.latest', '', 0, 8, 0, '<div class="tickets-latest-row[[+guest]]">\n	<span class="user"><i class="glyphicon glyphicon-user"></i> [[+fullname]]</span> <span class="date">[[+date_ago]]</span>\n	<br/>\n	<span class="ticket">\n		<a href="[[~[[+ticket.id]]]]#comment-[[+id]]">[[+ticket.pagetitle]]</a>\n	</span>\n	<nobr><i class="glyphicon glyphicon-comment"></i> <span class="comments">[[+comments]]</span></nobr>\n</div>\n<!--tickets_guest  ticket-comment-guest-->', 0, NULL, 0, 'core/components/tickets/elements/chunks/chunk.comment_latest.tpl'),
(18, 1, 0, 'tpl.Tickets.comment.email.owner', '', 0, 8, 0, '[[%ticket_comment_email_owner_intro?\n    &name=`[[+name]]`\n    &resource=`[[+resource]]`\n    &pagetitle=`[[+pagetitle]]`\n]]\n\n<pre>[[+text]]</pre>\n<br/><br/>\n\n<a href="[[~[[+resource]]?scheme=`full`]]#comment-[[+id]]">[[%ticket_email_reply]]</a>', 0, NULL, 0, 'core/components/tickets/elements/chunks/chunk.comment_email_owner.tpl'),
(19, 1, 0, 'tpl.Tickets.comment.email.reply', '', 0, 8, 0, '[[%ticket_comment_email_reply_intro?\n    &name=`[[+name]]`\n    &resource=`[[+resource]]`\n    &pagetitle=`[[+pagetitle]]`\n]]\n\n<pre>[[+text]]</pre>\n<br/><br/>\n<a href="[[~[[+resource]]?scheme=`full`]]#comment-[[+id]]">[[%ticket_email_reply]]</a>\n<br/><br/>\n\n[[%ticket_comment_email_reply_text]]\n<pre>[[+parent_text]]</pre>', 0, NULL, 0, 'core/components/tickets/elements/chunks/chunk.comment_email_reply.tpl'),
(20, 1, 0, 'tpl.Tickets.comment.email.subscription', '', 0, 8, 0, '[[%ticket_comment_email_subscription_intro?\n    &name=`[[+name]]`\n    &resource=`[[+resource]]`\n    &pagetitle=`[[+pagetitle]]`\n]]\n\n<pre>[[+text]]</pre>\n<br/><br/>\n\n<a href="[[~[[+resource]]?scheme=`full`]]#comment-[[+id]]">[[%ticket_email_view]]</a>', 0, NULL, 0, 'core/components/tickets/elements/chunks/chunk.comment_email_subscription.tpl'),
(21, 1, 0, 'tpl.Tickets.comment.email.bcc', '', 0, 8, 0, '[[%ticket_comment_email_bcc_intro?\n    &name=`[[+name]]`\n    &resource=`[[+resource]]`\n    &pagetitle=`[[+pagetitle]]`\n]]\n\n<pre>[[+text]]</pre>\n<br/><br/>\n\n<a href="[[~[[+resource]]?scheme=`full`]]#comment-[[+id]]">[[%ticket_email_view]]</a>\n', 0, NULL, 0, 'core/components/tickets/elements/chunks/chunk.comment_email_bcc.tpl'),
(22, 1, 0, 'tpl.Tickets.comment.email.unpublished', '', 0, 8, 0, '[[%ticket_comment_email_unpublished_intro?\n    &name=`[[+name]]`\n    &resource=`[[+resource]]`\n    &pagetitle=`[[+pagetitle]]`\n]]\n\n<pre>[[+text]]</pre>\n<br/><br/>\n\n<a href="[[+manager_url]]">[[%ticket_email_all_comments]]</a>', 0, NULL, 0, 'core/components/tickets/elements/chunks/chunk.comment_email_unpublished.tpl'),
(23, 1, 0, 'tpl.Tickets.comment.list.row', '', 0, 8, 0, '<div class="ticket-comment-row ticket-comment" id="comment-[[+id]]" data-id="[[+id]]">\n	<div class="ticket-comment-body[[+guest]]">\n		<div class="ticket-comment-header">\n			<img src="[[+avatar]]" class="ticket-avatar" alt="" />\n			<span class="ticket-comment-author">[[+fullname]]</span>\n			<span class="ticket-comment-createdon">[[+date_ago]]</span>[[+comment_was_edited]]\n			<span class="ticket-comment-link"><a href="[[+url]]#comment-[[+id]]">#</a></span>\n			<span class="ticket-comment-star[[+can_star]]">[[+stared]][[+unstared]]</span>\n\n			<span class="ticket-comment-rating[[+can_vote]][[+cant_vote]]">\n				<span class="rating[[+rating_positive]][[+rating_negative]]" title="[[%ticket_rating_total]] [[+rating_total]]: ↑[[+rating_plus]] [[%ticket_rating_and]] ↓[[+rating_minus]]">[[+rating]]</span>\n				<span class="vote plus[[+voted_plus]]" title="[[%ticket_like]]"><i class="glyphicon glyphicon-arrow-up"></i></span>\n				<span class="vote minus[[+voted_minus]]" title="[[%ticket_dislike]]"><i class="glyphicon glyphicon-arrow-down"></i></span>\n			</span>\n		</div>\n		<div class="ticket-comment-text">\n			[[+text]]\n		</div>\n	</div>\n\n	<a href="[[~[[+section.id]]]]" class="ticket-comment-section"><i class="glyphicon glyphicon-folder-open"></i> [[+section.pagetitle]]</a> &rarr;\n	<a href="[[~[[+ticket.id]]]]" class="ticket-comment-ticket">[[+ticket.pagetitle]]</a> &nbsp;\n	<span class="ticket-comment-comments"><i class="glyphicon glyphicon-comment"></i> [[+comments]]</span>\n</div>\n<!--tickets_comment_was_edited <span class="ticket-comment-edited">([[%ticket_comment_was_edited]])</span>-->\n<!--tickets_can_vote  active-->\n<!--tickets_cant_vote  inactive-->\n<!--tickets_rating_positive  positive-->\n<!--tickets_rating_negative  negative-->\n<!--tickets_voted_plus  voted-->\n<!--tickets_voted_minus  voted-->\n<!--tickets_guest  ticket-comment-guest-->\n<!--tickets_can_star  active-->\n<!--tickets_stared <i class="glyphicon glyphicon-star stared star"></i>-->\n<!--tickets_unstared <i class="glyphicon glyphicon-star unstared star"></i>-->', 0, NULL, 0, 'core/components/tickets/elements/chunks/chunk.comment_list_row.tpl'),
(24, 1, 0, 'tpl.Tickets.list.row', '', 0, 8, 0, '<div class="tickets-row">\n    <h3 class="title"><a href="[[~[[+id]]]]">[[+pagetitle]]</a></h3>\n	<div class="content">\n		[[+introtext]]<br/>\n		<a href="[[~[[+id]]]]#cut" class="btn btn-default ticket-read-more">[[%ticket_read_more]]</a>\n	</div>\n	<div class="ticket-meta row" data-id="[[+id]]">\n		<span class="col-md-5">\n			<i class="glyphicon glyphicon-calendar"></i> [[+date_ago]]\n			&nbsp;&nbsp;\n			<i class="glyphicon glyphicon-user"></i> [[+fullname]]\n		</span>\n		<span class="col-md-2"><a href="[[~[[+section.id]]]]"><i class="glyphicon glyphicon-folder-open"></i> [[+section.pagetitle]]</a></span>\n		<span class="col-md-3">\n			<span class="ticket-star[[+can_star]]">[[+stared]][[+unstared]] <span class="ticket-star-count">[[+stars]]</span></span>\n			&nbsp;&nbsp;\n			<i class="glyphicon glyphicon-eye-open"></i> [[+views]]\n			&nbsp;&nbsp;\n			<i class="glyphicon glyphicon-comment"></i> [[+comments]]  [[+new_comments]]\n		</span>\n		<span class="col-md-2 pull-right ticket-rating[[+active]][[+inactive]]">\n			<span class="vote plus[[+voted_plus]]" title="[[%ticket_like]]"><i class="glyphicon glyphicon-arrow-up"></i></span>\n			[[+can_vote]][[+cant_vote]]\n			<span class="vote minus[[+voted_minus]]" title="[[%ticket_dislike]]"><i class="glyphicon glyphicon-arrow-down"></i></span>\n		</span>\n	</div>\n</div>\n<!--tickets_can_vote <span class="vote rating" title="[[%ticket_refrain]]"><i class="glyphicon glyphicon-minus"></i></span>-->\n<!--tickets_cant_vote <span class="rating[[+rating_positive]][[+rating_negative]]" title="[[%ticket_rating_total]] [[+rating_total]]: ↑[[+rating_plus]] [[%ticket_rating_and]] ↓[[+rating_minus]]">[[+rating]]</span>-->\n<!--tickets_new_comments <span class="ticket-new-comments">+[[+new_comments]]</span>-->\n<!--tickets_active  active-->\n<!--tickets_inactive  inactive-->\n<!--tickets_voted_plus  voted-->\n<!--tickets_voted_minus  voted-->\n<!--tickets_rating_positive  positive-->\n<!--tickets_rating_negative  negative-->\n<!--tickets_can_star  active-->\n<!--tickets_stared <i class="glyphicon glyphicon-star stared star"></i>-->\n<!--tickets_unstared <i class="glyphicon glyphicon-star unstared star"></i>-->', 0, NULL, 0, 'core/components/tickets/elements/chunks/chunk.ticket_list_row.tpl'),
(25, 1, 0, 'tpl.Tickets.sections.row', '', 0, 8, 0, '<div class="section-row">\r\n	<h3 class="title"><a href="[[~[[+id]]]]">[[+pagetitle]]</a></h3>\r\n	<div class="content">\r\n		[[+introtext]]\r\n	</div>\r\n	<div class="section-meta row">\r\n		<div class="col-md-1"><i class="glyphicon glyphicon-th-list"></i> [[+tickets]]</div>\r\n		<div class="col-md-1"><i class="glyphicon glyphicon-eye-open"></i> [[+views]]</div>\r\n		<div class="col-md-1"><i class="glyphicon glyphicon-comment"></i> [[+comments]]</div>\r\n	</div>\r\n</div>', 0, NULL, 0, 'core/components/tickets/elements/chunks/chunk.ticket_sections_row.tpl'),
(26, 1, 0, 'tpl.Tickets.sections.wrapper', '', 0, 8, 0, '[[+modx.user.id:isloggedin:is=`1`:then=`\n<span class="tickets-subscribe pull-right">\n	<label for="tickets-subscribe" class="checkbox">\n		<input type="checkbox" name="" id="tickets-subscribe" value="1" data-id="[[*id]]" [[+subscribed:notempty=`checked`]] /> [[%tickets_section_notify]]\n	</label>\n</span>\n`:else=``]]\n\n<div class="tickets-list">\n	[[+output]]\n</div>', 0, NULL, 0, 'core/components/tickets/elements/chunks/chunk.ticket_sections_wrapper.tpl'),
(27, 1, 0, 'tpl.Tickets.meta', '', 0, 8, 0, '<div class="ticket-meta row" data-id="[[+id]]">\n	<span class="col-md-5">\n		<i class="glyphicon glyphicon-calendar"></i> [[+date_ago]]\n		&nbsp;&nbsp;\n		<i class="glyphicon glyphicon-user"></i> [[+fullname]]\n	</span>\n	<span class="col-md-2"><a href="[[~[[+section.id]]]]"><i class="glyphicon glyphicon-folder-open"></i> [[+section.pagetitle]]</a></span>\n	<span class="col-md-2">\n		<span class="ticket-star[[+can_star]]">[[+stared]][[+unstared]] <span class="ticket-star-count">[[+stars]]</span></span>\n		&nbsp;&nbsp;\n		<i class="glyphicon glyphicon-eye-open"></i> [[+views]]\n	</span>\n	<span class="col-md-2 pull-right ticket-rating[[+active]][[+inactive]]">\n		<span class="vote plus[[+voted_plus]]" title="[[%ticket_like]]">\n			<i class="glyphicon glyphicon-arrow-up"></i>\n		</span>\n		[[+can_vote]][[+cant_vote]]\n		<span class="vote minus[[+voted_minus]]" title="[[%ticket_dislike]]">\n			<i class="glyphicon glyphicon-arrow-down"></i>\n		</span>\n	</span>\n</div>\n[[+has_files]]\n\n<!--tickets_can_vote <span class="vote rating" title="[[%ticket_refrain]]"><i class="glyphicon glyphicon-minus"></i></span>-->\n<!--tickets_cant_vote <span class="rating[[+rating_positive]][[+rating_negative]]" title="[[%ticket_rating_total]] [[+rating_total]]: ↑[[+rating_plus]] [[%ticket_rating_and]] ↓[[+rating_minus]]">[[+rating]]</span>-->\n<!--tickets_active  active-->\n<!--tickets_inactive  inactive-->\n<!--tickets_voted_plus  voted-->\n<!--tickets_voted_minus  voted-->\n<!--tickets_rating_positive  positive-->\n<!--tickets_rating_negative  negative-->\n<!--tickets_has_files\n<ul class="ticket-files">\n	<strong>[[%ticket_uploaded_files]]:</strong>\n	[[+files]]\n</ul>-->\n<!--tickets_can_star  active-->\n<!--tickets_stared <i class="glyphicon glyphicon-star stared star"></i>-->\n<!--tickets_unstared <i class="glyphicon glyphicon-star unstared star"></i>-->', 0, NULL, 0, 'core/components/tickets/elements/chunks/chunk.ticket_meta.tpl'),
(28, 1, 0, 'tpl.Tickets.meta.file', '', 0, 8, 0, '<li>\n	<a href="[[+url]]" target="_blank">[[+name]]</a> [[+size]] kb\n</li>', 0, NULL, 0, 'core/components/tickets/elements/chunks/chunk.ticket_meta_file.tpl'),
(29, 0, 0, 'HEAD', 'Chunk', 0, 10, 0, '<head>\n    <base href="[[++site_url]]" />\n    <meta charset="[[++modx_charset]]" />\n    \n    <title>[[*meta_title::default=`[[*pagetitle]]`]]</title>\n    <meta name="description" content="[[*introtext:is=``:then=`Some description text`:else=`[[*introtext]]`]]">\n    <meta name="keywords" content="[[*meta_keywords::default=`Some keywords hare`]]">\n\n	<meta http-equiv="X-UA-Compatible" content="IE=edge">\n	<meta name="viewport" content="width=device-width, initial-scale=1, maximum-scale=1">\n	\n	<meta property="og:url"                content="[[~[[++site_start]]]][[*uri]]" />\n    <meta property="og:type"               content="website"/>\n    <meta property="og:title"              content="[[*meta_title::default=`[[*pagetitle]]`]]" />\n    <meta property="og:description"        content="[[*introtext:is=``:then=`Some description text`:else=`[[*introtext]]`]]" />\n    <meta property="og:image"              content="[[*meta_thumb::default=`[[*meta_thumb:is=``:then=`[[~[[++site_start]]]]design/img/tsumbaluk-in-ua.jpg`:else=`[[*meta_thumb]]`]]`]]" />\n\n    <link rel="shortcut icon" href="design/img/favicon.png" type="image/x-icon">\n	<link rel="stylesheet" href="design/css/vendor.min.css">\n	<link rel="stylesheet" href="design/css/header.min.css">\n	<link rel="stylesheet" href="design/css/main.min.css">\n</head>', 0, 'a:0:{}', 0, ''),
(30, 0, 0, 'HEADER', 'Chunk', 0, 10, 0, '<h1>[[++site_name]]</h1>', 0, 'a:0:{}', 0, ''),
