a:7:{s:4:"name";s:13:"useWeblinkUrl";s:4:"desc";s:27:"pdotools_prop_useWeblinkUrl";s:4:"type";s:13:"combo-boolean";s:7:"options";a:0:{}s:5:"value";b:1;s:7:"lexicon";s:19:"pdotools:properties";s:4:"area";s:0:"";}s:5:"cache";a:7:{s:4:"name";s:5:"cache";s:4:"desc";s:19:"pdotools_prop_cache";s:4:"type";s:13:"combo-boolean";s:7:"options";a:0:{}s:5:"value";b:1;s:7:"lexicon";s:19:"pdotools:properties";s:4:"area";s:0:"";}s:8:"cacheKey";a:7:{s:4:"name";s:8:"cacheKey";s:4:"desc";s:22:"pdotools_prop_cacheKey";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";s:19:"pdotools:properties";s:4:"area";s:0:"";}s:9:"cacheTime";a:7:{s:4:"name";s:9:"cacheTime";s:4:"desc";s:23:"pdotools_prop_cacheTime";s:4:"type";s:11:"numberfield";s:7:"options";a:0:{}s:5:"value";i:600;s:7:"lexicon";s:19:"pdotools:properties";s:4:"area";s:0:"";}}', '', 0, 'core/components/pdotools/elements/snippets/snippet.pdositemap.php'),
(35, 1, 0, 'pdoNeighbors', '', 0, 7, 0, '/** @var array $scriptProperties */\n/** @var pdoFetch $pdoFetch */\n$fqn = $modx->getOption(''pdoFetch.class'', null, ''pdotools.pdofetch'', true);\n$path = $modx->getOption(''pdofetch_class_path'', null, MODX_CORE_PATH . ''components/pdotools/model/'', true);\nif ($pdoClass = $modx->loadClass($fqn, $path, false, true)) {\n    $pdoFetch = new $pdoClass($modx, $scriptProperties);\n} else {\n    return false;\n}\n$pdoFetch->addTime(''pdoTools loaded'');\n\nif (empty($id)) {\n    $id = $modx->resource->id;\n}\nif (empty($limit)) {\n    $limit = 1;\n}\nif (!isset($outputSeparator)) {\n    $outputSeparator = "\\n";\n}\n$fastMode = !empty($fastMode);\n\n$class = ''modResource'';\n$resource = ($id == $modx->resource->id)\n    ? $modx->resource\n    : $modx->getObject($class, $id);\nif (!$resource) {\n    return '''';\n}\n\n// We need to determine ids of neighbors\n$params = $scriptProperties;\n$params[''select''] = ''id'';\n$params[''limit''] = 0;\nif (!empty($parents)) {\n    $parents = array_map(''trim'', explode('','', $parents));\n    if (!in_array($resource->parent, $parents)) {\n        $parents[] = $resource->parent;\n    }\n    $key = array_search($resource->parent * -1, $parents);\n    if ($key !== false) {\n        unset($parents[$key]);\n    }\n    $params[''parents''] = implode('','', $parents);\n    $ids = $pdoFetch->getCollection(''modResource'', array(), $params);\n    unset($scriptProperties[''parents'']);\n} else {\n    $ids = $pdoFetch->getCollection(''modResource'', array(''parent'' => $resource->parent), $params);\n}\n\n$found = false;\n$prev = $next = array();\nforeach ($ids as $v) {\n    if ($v[''id''] == $id) {\n        $found = true;\n        continue;\n    } elseif (!$found) {\n        $prev[] = $v[''id''];\n    } else {\n        $next[] = $v[''id''];\n        if (count($next) >= $limit) {\n            break;\n        }\n    }\n}\n$prev = array_splice($prev, $limit * -1);\nif (!empty($loop)) {\n    if (!count($prev)) {\n        $v = end($ids);\n        $prev[] = $v[''id''];\n    } else {\n        if (!count($next)) {\n            $v = reset($ids);\n            $next[] = $v[''id''];\n        }\n    }\n}\n$ids = array_merge($prev, $next, array($resource->parent));\n$pdoFetch->addTime(''Found ids of neighbors: '' . implode('','', $ids));\n\n// Query conditions\n$where = array($class . ''.id:IN'' => $ids);\n\n// Fields to select\n$resourceColumns = array_keys($modx->getFieldMeta($class));\nif (empty($includeContent) && empty($useWeblinkUrl)) {\n    $key = array_search(''content'', $resourceColumns);\n    unset($resourceColumns[$key]);\n}\n$select = array($class => implode('','', $resourceColumns));\n\n// Add custom parameters\nforeach (array(''where'', ''select'') as $v) {\n    if (!empty($scriptProperties[$v])) {\n        $tmp = $modx->fromJSON($scriptProperties[$v]);\n        if (is_array($tmp)) {\n            $$v = array_merge($$v, $tmp);\n        }\n    }\n    unset($scriptProperties[$v]);\n}\n$pdoFetch->addTime(''Conditions prepared'');\n\n// Default parameters\n$default = array(\n    ''class'' => $class,\n    ''where'' => $modx->toJSON($where),\n    ''select'' => $modx->toJSON($select),\n    //''groupby'' => $class.''.id'',\n    ''sortby'' => $class . ''.menuindex'',\n    ''sortdir'' => ''ASC'',\n    ''return'' => ''data'',\n    ''limit'' => 0,\n    ''totalVar'' => ''pdoneighbors.total'',\n);\n\n// Merge all properties and run!\nunset($scriptProperties[''limit'']);\n$pdoFetch->addTime(''Query parameters ready'');\n$pdoFetch->setConfig(array_merge($default, $scriptProperties), false);\n\n$rows = $pdoFetch->run();\n$prev = array_flip($prev);\n$next = array_flip($next);\n\n$output = array(''prev'' => array(), ''up'' => array(), ''next'' => array());\nforeach ($rows as $row) {\n    if (empty($row[''menutitle''])) {\n        $row[''menutitle''] = $row[''pagetitle''];\n    }\n    if (!empty($useWeblinkUrl) && $row[''class_key''] == ''modWebLink'') {\n        $row[''link''] = is_numeric(trim($row[''content''], ''[]~ ''))\n            ? $pdoFetch->makeUrl(intval(trim($row[''content''], ''[]~ '')), $row)\n            : $row[''content''];\n    } else {\n        $row[''link''] = $pdoFetch->makeUrl($row[''id''], $row);\n    }\n\n    if (isset($prev[$row[''id'']])) {\n        $output[''prev''][] = !empty($tplPrev)\n            ? $pdoFetch->getChunk($tplPrev, $row, $fastMode)\n            : $pdoFetch->getChunk('''', $row);\n    } elseif (isset($next[$row[''id'']])) {\n        $output[''next''][] = !empty($tplNext)\n            ? $pdoFetch->getChunk($tplNext, $row, $fastMode)\n            : $pdoFetch->getChunk('''', $row);\n    } else {\n        $output[''up''][] = !empty($tplUp)\n            ? $pdoFetch->getChunk($tplUp, $row, $fastMode)\n            : $pdoFetch->getChunk('''', $row);\n    }\n}\n$pdoFetch->addTime(''Chunks processed'');\n\n$log = '''';\nif ($modx->user->hasSessionContext(''mgr'') && !empty($showLog)) {\n    $log .= ''<pre class="pdoNeighborsLog">'' . print_r($pdoFetch->getTime(), 1) . ''</pre>'';\n}\n\nforeach ($output as &$row) {\n    $row = implode($outputSeparator, $row);\n}\n\nif (!empty($toSeparatePlaceholders)) {\n    $output[''log''] = $log;\n    $modx->setPlaceholders($output, $toSeparatePlaceholders);\n} else {\n    if (!empty($rows) || !empty($wrapIfEmpty)) {\n        $output = !empty($tplWrapper)\n            ? $pdoFetch->getChunk($tplWrapper, $output, $fastMode)\n            : $pdoFetch->getChunk('''', $output);\n    } else {\n        $output = '''';\n    }\n    $output .= $log;\n\n    if (!empty($toPlaceholder)) {\n        $modx->setPlaceholder($toPlaceholder, $output);\n    } else {\n        return $output;\n    }\n}', 0, 'a:27:{s:2:"id";a:7:{s:4:"name";s:2:"id";s:4:"desc";s:16:"pdotools_prop_id";s:4:"type";s:11:"numberfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";s:19:"pdotools:properties";s:4:"area";s:0:"";}s:5:"limit";a:7:{s:4:"name";s:5:"limit";s:4:"desc";s:29:"pdotools_prop_neighbors_limit";