(44, 1, 0, 'getTickets', '', 0, 8, 0, '/* @var array $scriptProperties */\n/* @var Tickets $Tickets */\n$Tickets = $modx->getService(''tickets'', ''Tickets'', $modx->getOption(''tickets.core_path'', null, $modx->getOption(''core_path'') . ''components/tickets/'') . ''model/tickets/'', $scriptProperties);\n$Tickets->initialize($modx->context->key, $scriptProperties);\n\n/** @var pdoFetch $pdoFetch */\n$pdoFetch = $modx->getService(''pdoFetch'');\n$pdoFetch->setConfig($scriptProperties);\n$pdoFetch->addTime(''pdoTools loaded'');\n\nif (isset($parents) && $parents === '''') {\n	$scriptProperties[''parents''] = $modx->resource->id;\n}\n\n$class = ''Ticket'';\n$where = array(''class_key'' => $class);\n\n//Filter by user\nif (!empty($user)) {\n	$user = array_map(''trim'', explode('','', $user));\n	$user_id = $user_username = array();\n	foreach ($user as $v) {\n		if (is_numeric($v)) {\n			$user_id[] = $v;\n		}\n		else {\n			$user_username[] = $v;\n		}\n	}\n	if (!empty($user_id) && !empty($user_username)) {\n		$where[] = ''(`User`.`id` IN ('' . implode('','', $user_id) . '') OR `User`.`username` IN (\\'''' . implode(''\\'',\\'''', $user_username) . ''\\''))'';\n	}\n	else {\n		if (!empty($user_id)) {\n			$where[''User.id:IN''] = $user_id;\n		}\n		else {\n			if (!empty($user_username)) {\n				$where[''User.username:IN''] = $user_username;\n			}\n		}\n	}\n}\n\n// Joining tables\n$leftJoin = array(\n	''Section'' => array(''class'' => ''TicketsSection'', ''on'' => ''`Section`.`id` = `Ticket`.`parent`''),\n	''User'' => array(''class'' => ''modUser'', ''on'' => ''`User`.`id` = `Ticket`.`createdby`''),\n	''Profile'' => array(''class'' => ''modUserProfile'', ''on'' => ''`Profile`.`internalKey` = `User`.`id`''),\n);\nif ($Tickets->authenticated) {\n	$leftJoin[''Vote''] = array(\n		''class'' => ''TicketVote'',\n		''on'' => ''`Vote`.`id` = `Ticket`.`id` AND `Vote`.`class` = "Ticket" AND `Vote`.`createdby` = '' . $modx->user->id\n	);\n	$leftJoin[''Star''] = array(\n		''class'' => ''TicketStar'',\n		''on'' => ''`Star`.`id` = `Ticket`.`id` AND `Star`.`class` = "Ticket" AND `Star`.`createdby` = '' . $modx->user->id\n	);\n}\n\n// Fields to select\n$select = array(\n	''Section'' => $modx->getSelectColumns(''TicketsSection'', ''Section'', ''section.'', array(''content''), true),\n	''User'' => $modx->getSelectColumns(''modUser'', ''User'', '''', array(''username'')),\n	''Profile'' => $modx->getSelectColumns(''modUserProfile'', ''Profile'', '''', array(''id''), true),\n	''Ticket'' => !empty($includeContent)\n		? $modx->getSelectColumns($class, $class)\n		: $modx->getSelectColumns($class, $class, '''', array(''content''), true),\n);\nif ($Tickets->authenticated) {\n	$select[''Vote''] = ''`Vote`.`value` as `vote`'';\n	$select[''Star''] = ''COUNT(`Star`.`id`) as `star`'';\n}\n$pdoFetch->addTime(''Conditions prepared'');\n\n// Add custom parameters\nforeach (array(''where'', ''select'', ''leftJoin'') as $v) {\n	if (!empty($scriptProperties[$v])) {\n		$tmp = $modx->fromJSON($scriptProperties[$v]);\n		if (is_array($tmp)) {\n			$$v = array_merge($$v, $tmp);\n		}\n	}\n	unset($scriptProperties[$v]);\n}\n\n$default = array(\n	''class'' => $class,\n	''where'' => $modx->toJSON($where),\n	''leftJoin'' => $modx->toJSON($leftJoin),\n	''select'' => $modx->toJSON($select),\n	''sortby'' => ''createdon'',\n	''sortdir'' => ''DESC'',\n	''groupby'' => $class . ''.id'',\n	''return'' => !empty($returnIds)\n		? ''ids''\n		: ''data'',\n	''nestedChunkPrefix'' => ''tickets_'',\n);\n\n// Merge all properties and run!\n$pdoFetch->setConfig(array_merge($default, $scriptProperties));\n$pdoFetch->addTime(''Query parameters are prepared.'');\n$rows = $pdoFetch->run();\n\nif (!empty($returnIds)) {\n	return $rows;\n}\n\n// Processing rows\n$output = array();\nif (!empty($rows) && is_array($rows)) {\n	foreach ($rows as $k => $row) {\n		// Handle properties\n		$properties = is_string($row[''properties''])\n			? $modx->fromJSON($row[''properties''])\n			: $row[''properties''];\n		if (!empty($properties[''tickets''])) {\n			$properties = $properties[''tickets''];\n		}\n		if (empty($properties[''process_tags''])) {\n			foreach ($row as $field => $value) {\n				$row[$field] = str_replace(\n					array(''['', '']'', ''`'', ''{'', ''}''),\n					array(''&#91;'', ''&#93;'', ''&#96;'', ''&#123;'', ''&#125;''),\n					$value\n				);\n			}\n		}\n		if (!is_array($properties)) {\n			$properties = array();\n		}\n\n		// Handle rating\n		$row[''rating''] = $row[''rating_total''] = array_key_exists(''rating'', $properties)\n			? $properties[''rating'']\n			: 0;\n		$row[''rating_plus''] = array_key_exists(''rating_plus'', $properties)\n			? $properties[''rating_plus'']\n			: 0;\n		$row[''rating_minus''] = array_key_exists(''rating_minus'', $properties)\n			? $properties[''rating_minus'']\n			: 0;\n		if ($row[''rating''] > 0) {\n			$row[''rating''] = ''+'' . $row[''rating''];\n			$row[''rating_positive''] = 1;\n		}\n		elseif ($row[''rating''] < 0) {\n			$row[''rating_negative''] = 1;\n		}\n\n		if (!$Tickets->authenticated || $modx->user->id == $row[''createdby'']) {\n			$row[''cant_vote''] = 1;\n		}\n		elseif (array_key_exists(''vote'', $row)) {\n			if ($row[''vote''] == '''') {\n				$row[''can_vote''] = 1;\n			}\n			elseif ($row[''vote''] > 0) {\n				$row[''voted_plus''] = 1;\n				$row[''cant_vote''] = 1;\n			}\n			elseif ($row[''vote''] < 0) {\n				$row[''voted_minus''] = 1;\n				$row[''cant_vote''] = 1;\n			}\n			else {\n				$row[''voted_none''] = 1;\n				$row[''cant_vote''] = 1;\n			}\n		}\n		$row[''active''] = (int)!empty($row[''can_vote'']);\n		$row[''inactive''] = (int)!empty($row[''cant_vote'']);\n\n		$row[''can_star''] = $Tickets->authenticated;\n		$row[''stared''] = !empty($row[''star'']);\n		$row[''unstared''] = empty($row[''star'']);\n\n		// Adding fields to row\n		$additional_fields = $pdoFetch->getObject(''Ticket'', $row[''id''], array(\n			''leftJoin'' => array(\n				''View'' => array(''class'' => ''TicketView'', ''on'' => ''`Ticket`.`id` = `View`.`parent`''),\n				''Thread'' => array(''class'' => ''TicketThread'', ''on'' => ''`Thread`.`resource` = `Ticket`.`id`  AND `Thread`.`deleted` = 0''),\n			),\n			''select'' => array(\n				''View'' => ''COUNT(`View`.`parent`) as `views`'',\n				''Thread'' => ''`Thread`.`id` as `thread`'',\n			),\n			''groupby'' => $class . ''.id''\n		));\n		$row = array_merge($row, $additional_fields);\n		$row[''date_ago''] = $Tickets->dateFormat($row[''createdon'']);\n		$row[''comments''] = $modx->getCount(''TicketComment'', array(''published'' => 1, ''thread'' => $row[''thread'']));\n		$row[''stars''] = $modx->getCount(''TicketStar'', array(''id'' => $row[''id''], ''class'' => ''Ticket''));\n		// Special fields for quick placeholders\n		$row[''isauthor''] = $modx->user->id == $row[''createdby''];\n		$row[''unpublished''] = empty($row[''published'']);\n\n		$row[''idx''] = $pdoFetch->idx++;\n		// Processing new comments\n		if ($Tickets->authenticated) {\n			$last_view = $pdoFetch->getObject(''TicketView'', array(\n				''parent'' => $row[''id''],\n				''uid'' => $modx->user->id,\n			), array(\n				''sortby'' => ''timestamp'',\n				''sortdir'' => ''DESC'',\n				''limit'' => 1,\n			));\n			if (!empty($last_view[''timestamp''])) {\n				$row[''new_comments''] = $modx->getCount(''TicketComment'', array(\n					''published'' => 1,\n					''thread'' => $row[''thread''],\n					''createdon:>'' => $last_view[''timestamp''],\n					''createdby:!='' => $modx->user->id,\n				));\n			}\n			else {\n				$row[''new_comments''] = $row[''comments''];\n			}\n		}\n		else {\n			$row[''new_comments''] = '''';\n		}\n\n		// Processing chunk\n		$tpl = $pdoFetch->defineChunk($row);\n		$output[] = empty($tpl)\n			? ''<pre>'' . $pdoFetch->getChunk('''', $row) . ''</pre>''\n			: $pdoFetch->getChunk($tpl, $row, $pdoFetch->config[''fastMode'']);\n	}\n}\n$pdoFetch->addTime(''Returning processed chunks'');\n\n$log = '''';\nif ($modx->user->hasSessionContext(''mgr'') && !empty($showLog)) {\n	$log .= ''<pre class="getTicketsLog">'' . print_r($pdoFetch->getTime(), 1) . ''</pre>'';\n}\n\n// Return output\nif (!empty($toSeparatePlaceholders)) {\n	$output[''log''] = $log;\n	$modx->setPlaceholders($output, $toSeparatePlaceholders);\n}\nelse {\n	if (empty($outputSeparator)) {\n		$outputSeparator = "\\n";\n	}\n	$output = implode($outputSeparator, $output);\n	$output .= $log;\n\n	if (!empty($tplWrapper) && (!empty($wrapIfEmpty) || !empty($output))) {\n		$array = array(''output'' => $output);\n		if ($Tickets->authenticated && $modx->resource->class_key == ''TicketsSection'') {\n			/** @var TicketsSection $section */\n			$section = &$modx->resource;\n			$array[''subscribed''] = $section->isSubscribed();\n		}\n		$output = $pdoFetch->getChunk($tplWrapper, $array, $pdoFetch->config[''fastMode'']);\n	}\n\n	if (!empty($toPlaceholder)) {\n		$modx->setPlaceholder($toPlaceholder, $output);\n	}\n	else {\n		return $output;\n	}\n}', 0, 'a:21:{s:3:"tpl";a:7:{s:4:"name";s:3:"tpl";s:4:"desc";s:16:"tickets_prop_tpl";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:20:"tpl.Tickets.list.row";s:7:"lexicon";s:18:"tickets:properties";s:4:"area";s:0:"";}s:5:"limit";a:7:{s:4:"name";s:5:"limit";s:4:"desc";s:18:"tickets_prop_limit";s:4:"type";s:11:"numberfield";s:7:"options";a:0:{}s:5:"value";i:10;s:7:"lexicon";s:18:"tickets:properties";s:4:"area";s:0:"";}s:6:"offset";a:7:{s:4:"name";s:6:"offset";s:4:"desc";s:19:"tickets_prop_offset";s:4:"type";s:11:"numberfield";s:7:"options";a:0:{}s:5:"value";i:0;s:7:"lexicon";s:18:"tickets:properties";s:4:"area";s:0:"";}s:5:"depth";a:7:{s:4:"name";s:5:"depth";s:4:"desc";s:18:"tickets_prop_depth";s:4:"type";s:11:"numberfield";s:7:"options";a:0:{}s:5:"value";i:10;s:7:"lexicon";s:18:"tickets:properties";s:4:"area";s:0:"";}s:7:"parents";a:7:{s:4:"name";s:7:"parents";s:4:"desc";s:20:"tickets_prop_parents";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";s:18:"tickets:properties";s:4:"area";s:0:"";}s:9:"resources";a:7:{s:4:"name";s:9:"resources";s:4:"desc";s:22:"tickets_prop_resources";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";s:18:"tickets:properties";s:4:"area";s:0:"";}s:6:"sortby";a:7:{s:4:"name";s:6:"sortby";s:4:"desc";s:19:"tickets_prop_sortby";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:9:"createdon";s:7:"lexicon";s:18:"tickets:properties";s:4:"area";s:0:"";}s:7:"sortdir";a:7:{s:4:"name";s:7:"sortdir";s:4:"desc";s:20:"tickets_prop_sortdir";s:4:"type";s:4:"list";s:7:"options";a:2:{i:0;a:2:{s:4:"text";s:3:"ASC";s:5:"value";s:3:"ASC";}i:1;a:2:{s:4:"text";s:4:"DESC";s:5:"value";s:4:"DESC";}}s:5:"value";s:4:"DESC";s:7:"lexicon";s:18:"tickets:properties";s:4:"area";s:0:"";}s:14:"includeContent";a:7:{s:4:"name";s:14:"includeContent";s:4:"desc";s:27:"tickets_prop_includeContent";s:4:"type";s:13:"combo-boolean";s:7:"options";a:0:{}s:5:"value";b:0;s:7:"lexicon";s:18:"tickets:properties";s:4:"area";s:0:"";}s:13:"toPlaceholder";a:7:{s:4:"name";s:13:"toPlaceholder";s:4:"desc";s:26:"tickets_prop_toPlaceholder";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";s:18:"tickets:properties";s:4:"area";s:0:"";}s:10:"includeTVs";a:7:{s:4:"name";s:10:"includeTVs";s:4:"desc";s:23:"tickets_prop_includeTVs";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";s:18:"tickets:properties";s:4:"area";s:0:"";}s:5:"where";a:7:{s:4:"name";s:5:"where";s:4:"desc";s:18:"tickets_prop_where";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";s:18:"tickets:properties";s:4:"area";s:0:"";}s:8:"tvPrefix";a:7:{s:4:"name";s:8:"tvPrefix";s:4:"desc";s:21:"tickets_prop_tvPrefix";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";s:18:"tickets:properties";s:4:"area";s:0:"";}s:15:"outputSeparator";a:7:{s:4:"name";s:15:"outputSeparator";s:4:"desc";s:28:"tickets_prop_outputSeparator";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:1:"\n";s:7:"lexicon";s:18:"tickets:properties";s:4:"area";s:0:"";}s:7:"showLog";a:7:{s:4:"name";s:7:"showLog";s:4:"desc";s:20:"tickets_prop_showLog";s:4:"type";s:13:"combo-boolean";s:7:"options";a:0:{}s:5:"value";b:0;s:7:"lexicon";s:18:"tickets:properties";s:4:"area";s:0:"";}s:8:"fastMode";a:7:{s:4:"name";s:8:"fastMode";s:4:"desc";s:21:"tickets_prop_fastMode";s:4:"type";s:13:"combo-boolean";s:7:"options";a:0:{}s:5:"value";b:0;s:7:"lexicon";s:18:"tickets:properties";s:4:"area";s:0:"";}s:15:"showUnpublished";a:7:{s:4:"name";s:15:"showUnpublished";s:4:"desc";s:28:"tickets_prop_showUnpublished";s:4:"type";s:13:"combo-boolean";s:7:"options";a:0:{}s:5:"value";b:0;s:7:"lexicon";s:18:"tickets:properties";s:4:"area";s:0:"";}s:11:"showDeleted";a:7:{s:4:"name";s:11:"showDeleted";s:4:"desc";s:24:"tickets_prop_showDeleted";s:4:"type";s:13:"combo-boolean";s:7:"options";a:0:{}s:5:"value";b:0;s:7:"lexicon";s:18:"tickets:properties";s:4:"area";s:0:"";}s:10:"showHidden";a:7:{s:4:"name";s:10:"showHidden";s:4:"desc";s:23:"tickets_prop_showHidden";s:4:"type";s:13:"combo-boolean";s:7:"options";a:0:{}s:5:"value";b:1;s:7:"lexicon";s:18:"tickets:properties";s:4:"area";s:0:"";}s:4:"user";a:7:{s:4:"name";s:4:"user";s:4:"desc";s:17:"tickets_prop_user";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";s:18:"tickets:properties";s:4:"area";s:0:"";}s:10:"tplWrapper";a:7:{s:4:"name";s:10:"tplWrapper";s:4:"desc";s:23:"tickets_prop_tplWrapper";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";s:18:"tickets:properties";s:4:"area";s:0:"";}}', '', 0, 'core/components/tickets/elements/snippets/snippet.get_tickets.php'),
(45, 1, 0, 'getTicketsSections', '', 0, 8, 0, '/* @var array $scriptProperties */\n/* @var Tickets $Tickets */\n$Tickets = $modx->getService(''tickets'', ''Tickets'', $modx->getOption(''tickets.core_path'', null, $modx->getOption(''core_path'') . ''components/tickets/'') . ''model/tickets/'', $scriptProperties);\n$Tickets->initialize($modx->context->key, $scriptProperties);\n\n/** @var pdoFetch $pdoFetch */\n$pdoFetch = $modx->getService(''pdoFetch'');\n$pdoFetch->setConfig($scriptProperties);\n$pdoFetch->addTime(''pdoTools loaded'');\n\nif (isset($parents) && $parents === '''') {\n	$scriptProperties[''parents''] = $modx->resource->id;\n}\n\n$class = ''TicketsSection'';\n$where = array(''class_key'' => $class);\n\n// Adding custom where parameters\nif (!empty($scriptProperties[''where''])) {\n	$tmp = $modx->fromJSON($scriptProperties[''where'']);\n	if (is_array($tmp)) {\n		$where = array_merge($where, $tmp);\n	}\n}\nunset($scriptProperties[''where'']);\n$pdoFetch->addTime(''"Where" expression built.'');\n\n// Joining tables\n$leftJoin = array(\n	''Ticket'' => array(''class'' => ''Ticket'', ''on'' => ''Ticket.parent=TicketsSection.id AND Ticket.published=1 AND Ticket.deleted=0 AND Ticket.class_key="Ticket"''),\n	''View'' => array(''class'' => ''TicketView'', ''on'' => ''Ticket.id=View.parent''),\n	//''TicketVote'' => array(''class'' => ''TicketVote'', ''on'' => ''icket.id=Vote.parent AND Vote.class="Ticket"''),\n);\n\n// Fields to select\n$select = array(\n	''TicketsSection'' => !empty($includeContent)\n		? $modx->getSelectColumns($class, $class)\n		: $modx->getSelectColumns($class, $class, '''', array(''content''), true),\n	''Ticket'' => ''COUNT(DISTINCT `Ticket`.`id`) as `tickets`'',\n	''View'' => ''COUNT(`View`.`parent`) as `views`'',\n	//,''Vote'' => ''SUM(DISTINCT `Vote`.`value`) as `votes`''\n);\n\n$default = array(\n	''class'' => $class,\n	''where'' => $modx->toJSON($where),\n	''leftJoin'' => $modx->toJSON($leftJoin),\n	''select'' => $modx->toJSON($select),\n	''groupby'' => $class . ''.id'',\n	''sortby'' => ''views'',\n	''sortdir'' => ''DESC'',\n	''return'' => !empty($returnIds)\n		? ''ids''\n		: ''data'',\n	''nestedChunkPrefix'' => ''tickets_'',\n);\n\n// Merge all properties and run!\n$pdoFetch->setConfig(array_merge($default, $scriptProperties));\n$pdoFetch->addTime(''Query parameters are prepared.'');\n$rows = $pdoFetch->run();\n\nif (!empty($returnIds)) {\n	return $rows;\n}\n\n// Processing rows\n$output = array();\nif (!empty($rows) && is_array($rows)) {\n	foreach ($rows as $k => $row) {\n		// Processing main fields\n		$add = $pdoFetch->getObject(''TicketThread'', array(''deleted'' => 0), array(\n			''innerJoin'' => array(\n				''Ticket'' => array(''class'' => ''Ticket'', ''on'' => ''Ticket.id = TicketThread.resource AND Ticket.published=1 AND Ticket.deleted=0 AND Ticket.class_key="Ticket" AND Ticket.parent='' . $row[''id'']),\n			),\n			''select'' => array(\n				''TicketThread'' => ''SUM(TicketThread.comments) as `comments`''\n			)\n		));\n\n		$row[''comments''] = !empty($add[''comments''])\n			? $add[''comments'']\n			: 0;\n		$row[''date_ago''] = $Tickets->dateFormat($row[''createdon'']);\n\n		$row[''idx''] = $pdoFetch->idx++;\n		// Processing chunk\n		$tpl = $pdoFetch->defineChunk($row);\n		$output[] = empty($tpl)\n			? ''<pre>'' . $pdoFetch->getChunk('''', $row) . ''</pre>''\n			: $pdoFetch->getChunk($tpl, $row, $pdoFetch->config[''fastMode'']);\n	}\n}\n$pdoFetch->addTime(''Returning processed chunks'');\nif (empty($outputSeparator)) {\n	$outputSeparator = "\\n";\n}\n$output = implode($outputSeparator, $output);\n\n$log = '''';\nif ($modx->user->hasSessionContext(''mgr'') && !empty($showLog)) {\n	$log .= ''<pre class="getSectionsLog">'' . print_r($pdoFetch->getTime(), 1) . ''</pre>'';\n}\n\n// Return output\nif (!empty($toSeparatePlaceholders)) {\n	$output[''log''] = $log;\n	$modx->setPlaceholders($output, $toSeparatePlaceholders);\n}\nelse {\n	$output .= $log;\n\n	if (!empty($tplWrapper) && (!empty($wrapIfEmpt