(47, 1, 0, 'getStars', '', 0, 8, 0, '/** @var array $scriptProperties */\nif (empty($class)) {\n	$class = ''Ticket'';\n}\n/** @var integer $user */\nif (empty($user)) {\n	$user = $modx->user->get(''id'');\n}\nunset($scriptProperties[''user'']);\n\n$ids = array();\n$q = $modx->newQuery(''TicketStar'', array(''class'' => $class, ''createdby'' => $user));\n$q->select(''id'');\n$tstart = microtime(true);\nif ($q->prepare() && $q->stmt->execute()) {\n	$modx->queryTime = microtime(true) - $tstart;\n	$modx->executedQueries++;\n\n	$ids = $q->stmt->fetchAll(PDO::FETCH_COLUMN);\n}\n\nif (empty($ids)) {\n	return false;\n}\n\n$where = array($class . ''.id:IN'' => $ids);\nif (!empty($scriptProperties[''where''])) {\n	$tmp = $modx->fromJSON($scriptProperties[''where'']);\n	if (is_array($tmp)) {\n		$where = array_merge($where, $tmp);\n	}\n}\n$scriptProperties[''where''] = $modx->toJSON($where);\nif (empty($parents)) {\n	$scriptProperties[''parents''] = 0;\n}\nif (empty($tpl)) {\n	unset($scriptProperties[''tpl'']);\n}\n\nreturn $class == ''Ticket''\n	? $modx->runSnippet(''getTickets'', $scriptProperties)\n	: $modx->runSnippet(''getComments'', $scriptProperties);', 0, 'a:2:{s:5:"class";a:7:{s:4:"name";s:5:"class";s:4:"desc";s:18:"tickets_prop_class";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:6:"Ticket";s:7:"lexicon";s:18:"tickets:properties";s:4:"area";s:0:"";}s:3:"tpl";a:7:{s:4:"name";s:3:"tpl";s:4:"desc";s:16:"tickets_prop_tpl";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";s:18:"tickets:properties";s:4:"area";s:0:"";}}', '', 0, 'core/components/tickets/elements/snippets/snippet.get_stars.php');
