  `owner` int(10) NOT NULL DEFAULT '0',
  `rating` decimal(12,2) DEFAULT '0.00',
  `multiplier` int(10) DEFAULT '1',
  `ticket` int(10) NOT NULL DEFAULT '0',
  `section` int(10) NOT NULL DEFAULT '0',
  `createdby` int(10) NOT NULL DEFAULT '0',
